DictClient client = new DictClient();
Iterator it = client.getDefinitions(DictClient.DATABASE_ALL, "hello").iterator();
System.out.println("Definitions of word 'Hello':");
while(it.hasNext()) {
  DefinitionResponse dr = (DefinitionResponse)it.next();
  System.out.println(dr.getTextualInformation());
}
